mapper.select:
-- select * from app_info select a.id, a.softwareName,a.APKName,a.supportROM,a.softwareSize,a.devId, (select devName from dev_user where id = a.devId) as devName, a.status, (select valueName from data_dictionary d where a.status=d.valueId AND d.typeCode='APP_STATUS') as statusName, a.flatformId, (select valueName from data_dictionary d where a.flatformId=d.valueId AND d.typeCode='APP_FLATFORM') as flatformName, a.categoryLevel1, (select categoryName from app_category c where c.id=a.categoryLevel1) as categoryLevel1Name, a.categoryLevel2, (select categoryName from app_category c where c.id=a.categoryLevel2) as categoryLevel2Name, a.categoryLevel3, (select categoryName from app_category c where c.id=a.categoryLevel3) as categoryLevel3Name, a.downloads, a.onSaleDate, a.versionId, (select v.versionNo from app_version v where v.id=a.versionId ) as versionNo from app_info a
and softwareName like CONCAT('%',#{softwareName},'%')
and APKName =#{APKName}
and flatformId =#{flatformId}
and categoryLevel1 =#{categoryLevel1}
limit #{from},#{pageSize}

select COUNT(1) from app_info


        and softwareName like CONCAT('%',#{softwareName},'%')


        and APKName =#{APKName}


        and flatformId =#{flatformId}


        and categoryLevel1 =#{categoryLevel1}

mapper.update:
update app_info
softwareName=#{softwareName}, APKName=#{APKName}, supportROM=#{supportROM}, interfaceLanguage=#{interfaceLanguage}, softwareSize=#{softwareSize}, appInfo=#{appInfo}
where id=#{id}
